// Helper function to validate and format phone number to UK +44 format
function formatUKPhone(phone) {
  if (!phone) return '';

  // Remove all spaces, dashes, brackets
  let cleaned = phone.replace(/[\s\-\(\)]/g, '');

  // Check if it's a valid UK number format
  let validFormats = [
    /^(\+44|44)[1-9]\d{8,9}$/, // +44 or 44 format
    /^0[1-9]\d{8,9}$/ // 0 format
  ];

  let isValid = validFormats.some(regex => regex.test(cleaned));
  if (!isValid) return ''; // Return empty if invalid

  // Format to +44 standard
  if (cleaned.startsWith('+44')) {
    return cleaned;
  } else if (cleaned.startsWith('44')) {
    return '+' + cleaned;
  } else if (cleaned.startsWith('0')) {
    return '+44' + cleaned.substring(1);
  }

  return ''; // Return empty if can't format properly
}

// Helper function to validate email format
function validateEmail(email) {
  if (!email) return false;

  // Basic email validation regex
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email.trim());
}

// Helper function to parse flexible date formats to YYYY-MM-DD
function parseFlexibleDate(dateInput) {
  if (!dateInput) return '';

  const today = new Date();
  const input = dateInput.toLowerCase().trim();

  // Handle relative dates
  if (input.includes('tomorrow')) {
    const tomorrow = new Date(today);
    tomorrow.setDate(today.getDate() + 1);
    return tomorrow.toISOString().split('T')[0];
  }

  if (input.includes('next week')) {
    const nextWeek = new Date(today);
    nextWeek.setDate(today.getDate() + 7);
    return nextWeek.toISOString().split('T')[0];
  }

  if (input.match(/in (\d+) days?/)) {
    const days = parseInt(input.match(/in (\d+) days?/)[1]);
    const futureDate = new Date(today);
    futureDate.setDate(today.getDate() + days);
    return futureDate.toISOString().split('T')[0];
  }

  // Try to parse standard date formats
  const possibleDate = new Date(dateInput);
  if (!isNaN(possibleDate.getTime())) {
    return possibleDate.toISOString().split('T')[0];
  }

  // If already in YYYY-MM-DD format, return as is
  if (dateInput.match(/^\d{4}-\d{2}-\d{2}$/)) {
    return dateInput;
  }

  // Return original if can't parse
  return dateInput;
}

